mapper.select:
SELECT ll.exp_no ,DATE_FORMAT(ll.exp_date, '%Y-%m-%d') as exp_date ,DATE_FORMAT(ll.exp_sdate, '%Y-%m-%d') as exp_sdate ,exac.acc_nm ,ll.loginID ,ui.name ,ll.dac_nm ,(select dept_name from tb_dept where ll.dept_cd = tb_dept.dept_cd )as dept_name ,ll.exp_cost ,CASE WHEN ll.exp_stat = '1' THEN '승인대기중' WHEN ll.exp_stat = '2' THEN '승인' ELSE '반려' END as exp_stat FROM tb_expend_account exac RIGHT JOIN ( SELECT expe.exp_no , expe.exp_date , expe.exp_sdate , exdt.acc_no , exdt.dac_nm , expe.loginID , expe.dept_cd , expe.exp_cost , expe.exp_stat , expe.dac_no FROM tb_expend expe INNER JOIN tb_expend_dtlaccount exdt ON expe.acc_no = exdt.acc_no and expe.dac_no = exdt.dac_no ) ll ON ll.acc_no = exac.acc_no left outer join tb_userinfo ui on ui.loginID = ll.loginID
and exp_date >= STR_TO_DATE(#{from_date}, '%Y-%m-%d')
and exp_date <= STR_TO_DATE(#{to_date}, '%Y-%m-%d')
and ll.acc_no = #{accall}
and ll.dac_no = #{accdall}
and ll.exp_stat = #{apptype}
ORDER BY exp_no desc LIMIT #{pageIndex}, #{pageSize}

SELECT COUNT(1) AS tot_cnt FROM ( select ll.exp_no ,ll.exp_date ,ll.exp_sdate ,ll.loginID ,exac.acc_nm ,ll.dac_nm ,ll.acc_no ,ll.dac_no ,ll.exp_stat as ext_tt ,(select dept_name from tb_dept where ll.dept_cd = tb_dept.dept_cd )as dept_name ,ll.exp_cost ,CASE WHEN ll.exp_stat = '1' THEN '승인대기중' WHEN ll.exp_stat = '2' THEN '승인' ELSE '반려' END as exp_stat from tb_expend_account exac right join ( select expe.exp_no , expe.exp_date , expe.exp_sdate , exdt.acc_no , exdt.dac_nm , expe.dept_cd , expe.loginID , expe.exp_cost , expe.exp_stat , expe.dac_no from tb_expend expe inner join tb_expend_dtlaccount exdt on expe.acc_no = exdt.acc_no and expe.dac_no = exdt.dac_no ) ll on ll.acc_no = exac.acc_no left outer join tb_userinfo ui on ui.loginID = ll.loginID ) total
and exp_date >= STR_TO_DATE(#{from_date}, '%Y-%m-%d')
and exp_date <= STR_TO_DATE(#{to_date}, '%Y-%m-%d')
and acc_no = #{accall}
and dac_no = #{accdall}
and total.ext_tt = #{apptype}

mapper.update:
	update tb_expend
	   set exp_stat = #{exp_stat}
	      ,exp_pdate = #{exp_pdate}
	      ,exp_rere = #{exp_rere}
	      ,exp_bo = #{exp_bo}
     where exp_no = #{expend_num}
