mapper.select:
SET @rownum:=0;
SELECT @rownum:=@rownum+1 as row_num ,CASE WHEN vr.vac_type = '1' THEN '반차' WHEN vr.vac_type = '2' THEN '월차' ELSE '연차' END as vac_type ,ui.loginID ,ui.name ,DATE_FORMAT(vr.vac_sdate, '%Y-%m-%d') as vac_sdate ,DATE_FORMAT(vr.vac_edate, '%Y-%m-%d') as vac_edate ,vr.vac_bo ,CASE WHEN vr.vac_stat = '1' THEN '승인대기중' WHEN vr.vac_stat = '2' THEN '승인' ELSE '반려' END as vac_stat FROM tb_vacation_request vr LEFT JOIN tb_userinfo ui ON vr.loginID = ui.loginID
and vac_sdate >= STR_TO_DATE(#{from_date}, '%Y-%m-%d')
and vac_edate <= STR_TO_DATE(#{to_date}, '%Y-%m-%d')
and vac_type = #{vactype}
and vac_stat = #{apptype}
ORDER BY row_num LIMIT #{pageIndex}, #{pageSize}

SELECT COUNT(1) AS tot_cnt from tb_vacation_request vr left join tb_userinfo ui on vr.loginID = ui.loginID
and vac_sdate >= STR_TO_DATE(#{from_date}, '%Y-%m-%d')
and vac_edate <= STR_TO_DATE(#{to_date}, '%Y-%m-%d')
and vac_type = #{vactype}
and vac_stat = #{apptype}
