mapper.select:
/*kr.happy.study.sales.dao.DdRevenueDao.DdRevenueModel*/ select DATE_FORMAT(dor.do_date, '%Y-%m-%d') as do_date , cop.cop_no , cop.cop_nm , cop.loginID , bt.bi_nm , sit.si_nm , sit.si_price , dor.do_nmt , sit.si_price * dor.do_nmt as total from tb_order o inner join tb_dtlor dor on o.ord_no = dor.ord_no inner join tb_company cop on o.cop_no = cop.cop_no inner join tb_sitem sit on sit.si_cd = dor.si_cd inner join tb_bitem bt on bt.bi_cd = sit.bi_cd
and o.cop_no like (#{cilall})
and do_date >= STR_TO_DATE(#{from_date}, '%Y-%m-%d')
and do_date <= STR_TO_DATE(#{to_date}, '%Y-%m-%d')
order by o.ord_no , bt.bi_nm DESC limit #{pageIndex}, #{pageSize}
