mapper.select:
/*kr.happy.study.sales.dao.MmRevenueDao.MmRevenueModel*/ select a.do_date , a.total from ( select DATE_FORMAT(dor.do_date, '%Y-%m') as do_date , cop.cop_nm , bt.bi_nm , sit.si_price * dor.do_nmt as total from tb_order o inner join tb_dtlor dor on o.ord_no = dor.ord_no inner join tb_company cop on o.cop_no = cop.cop_no inner join tb_sitem sit on sit.si_cd = dor.si_cd inner join tb_bitem bt on bt.bi_cd = sit.bi_cd )a
and do_date >= STR_TO_DATE(#{from_month}, '%Y-%m')
and do_date <= STR_TO_DATE(#{to_month}, '%Y-%m')
group by a.do_date order by do_date DESC limit #{pageIndex}, #{pageSize}

/*kr.happy.study.sales.dao.MmRevenueDao.MmRevenueModel*/ select DATE_FORMAT(dor.do_date, '%Y-%m') as do_date from tb_dtlor dor group by DATE_FORMAT(dor.do_date, '%Y-%m')
and do_date >= STR_TO_DATE(#{from_month}, '%Y-%m')
and do_date <= STR_TO_DATE(#{to_month}, '%Y-%m')
